from django.db import modelsfrom master_serv.models.base_model import BaseModelclass Category(BaseModel):    category_id = models.AutoField(verbose_name='Id', primary_key=True)    code = models.CharField(verbose_name='code', max_length=5, blank=False, null=False)    description = models.CharField(verbose_name='Description', max_length=30, blank=False, null=False)    order = models.IntegerField(verbose_name='Order', blank=False, null=False)    class Meta:        db_table = 'warehouse_category'        ordering = ['category_id']        verbose_name = 'Category'        verbose_name_plural = 'Categories'    def __str__(self):        return '{}'.format(str(self.code) + '-' + str(self.description))