from django.db import modelsfrom apps.warehouse.models.category_model import Categoryfrom master_serv.models.base_model import BaseModelclass Subcategory(BaseModel):    subcategory_id = models.AutoField(verbose_name='Id', primary_key=True)    category = models.ForeignKey(Category, on_delete=models.DO_NOTHING, db_column='category_id',                                 verbose_name='Category', null=False)    code = models.CharField(verbose_name='code', max_length=5, blank=False, null=False)    description = models.CharField(verbose_name='Description', max_length=30, blank=False, null=False)    order = models.IntegerField(verbose_name='Order', blank=False, null=False)    class Meta:        db_table = 'warehouse_subcategory'        ordering = ['subcategory_id']        verbose_name = 'Sub Category'        verbose_name_plural = 'Sub Categories'    def __str__(self):        return '{}'.format(str(self.code) + '-' + str(self.description))