from apps.warehouse.models.brand_model import Brandclass BrandRepository:    @staticmethod    def get_brand(brand_id):        return Brand.objects.get_one(brand_id)    @staticmethod    def get_brands(*values, **params):        return Brand.objects.get_many(*values, **params)    @staticmethod    def create_brand(brand):        return Brand.objects.create_one(**brand)    @staticmethod    def update_brad(brand_id, brand):        return Brand.objects.update_one(obj_primary_key=brand_id, **brand)    @staticmethod    def log_delete_brand(brand_id):        return Brand.objects.log_delete_one(primary_key=brand_id)    @staticmethod    def soft_delete_brand(brand_id):        return Brand.objects.soft_delete_one(primary_key=brand_id)