from rest_framework import serializersfrom apps.warehouse.models.brand_model import Brandfrom master_serv.serializers.dynamic_field_serializer import DynamicFieldsModelSerializerclass BrandSerializer(serializers.ModelSerializer):    class Meta:        model = Brand        fields = '__all__'class BrandDynamicResponse(DynamicFieldsModelSerializer):    class Meta:        model = Brand        exclude = ('status', 'modified')class BrandDynamicRequest(DynamicFieldsModelSerializer):    class Meta:        model = Brand        fields = ('name',)class BrandBasicSerializer(serializers.ModelSerializer):    class Meta:        model = Brand        fields = ('name',)class BrandCreateRequest(serializers.ModelSerializer):    class Meta:        model = Brand        exclude = ('status', 'modified')class BrandUpdateRequest(serializers.ModelSerializer):    class Meta:        model = Brand        exclude = ("brand_id", "status", "modified")